import 'package:dots_indicator/dots_indicator.dart';
import 'package:eccommerce2/common/values/colors.dart';
import 'package:eccommerce2/main.dart';
import 'package:eccommerce2/pages/welcome/bloc/bloc.dart';
import 'package:eccommerce2/pages/welcome/bloc/events.dart';
import 'package:eccommerce2/pages/welcome/bloc/state.dart';
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';

class WelocomePage extends StatefulWidget {
  const WelocomePage({Key? key}) : super(key: key);

  @override
  State<WelocomePage> createState() => _WelocomePageState();
}

class _WelocomePageState extends State<WelocomePage> {
  late bool _splitScreenMode;
  PageController _pageController=PageController(initialPage: 0);

  @override
  void initState() {
    _splitScreenMode = true;
    super.initState();
  }
  @override
  Widget build(BuildContext context) {
    return Scaffold(body:Container(
      height: 375.w,
      // color: Colors.red,
      child: BlocBuilder<WelcomePagebloc,WelomePageState>(builder: (context, state) {
        return  Stack(
          alignment: Alignment.bottomCenter,
          children: [
            PageView(
              controller: _pageController,
              onPageChanged: (index){
                state.page=index;
                BlocProvider.of<WelcomePagebloc>(context).add(WelocmePageEvents());
              },
              children: [
                _pages(
                    1,
                    context,
                    "data:image/jpeg;base64,",
                    "next",
                    "First see Learning",
                    "Every body is  safe in their on position in  stages of life the living"
                ),
                _pages(
                    2,
                    context,
                    "data:image/jpeg;base64,",
                    "next",
                    "Connect with every one",
                    "Allways keep touch in with  your tutor & friends.lets get connected"
                ),
                _pages(
                    3,
                    context,
                    "https://th.bing.com/th/id/OIP.bGHT74E8zGbnYx5n63rSLAHaE7?w=269&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7",
                    "Get started",
                    "All ways facinated learning",
                    "Every body is  safe in their on position in  stages of life the living"
                ),


              ],
            ),
            Positioned(
                bottom: 100.h,
                child: DotsIndicator(
                  position: state.page,

                    dotsCount: 3,
                    decorator: DotsDecorator(
                      color: Colors.grey,
                      activeSize: Size(15,8),
                      activeColor:AppColors.primaryElement,
                      activeShape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(6)
                      ),
                      size: Size.square(8.0,),
                    )))

          ],
        );
      },)
     
    )
    );
  }
  Widget _pages(int index, BuildContext ctx,String image,String buttonName,String title,String desc){

    return  Column(
      children: [
        Container(
          color: Colors.white,
          width: 80.w,
          height:240.h,
          child: Image.network(image)
        ),
        Container(
          child: Text(title,style: TextStyle(
              color: AppColors.primaryText,fontSize: 18.sp,fontWeight: FontWeight.normal
          ),),
        ),
        Container(
          padding: EdgeInsets.symmetric(horizontal: 30.w),
          child: Text(desc,style: TextStyle(
              color: Colors.black.withOpacity(.5),fontSize: 10.sp,fontWeight: FontWeight.normal
          ),),
        ),
        InkWell(
          onTap: (){
            if(index<3){
              _pageController.animateToPage(index, duration: const Duration(microseconds: 500), curve: Curves.bounceIn);

            }
            else{
              // Navigator.push(
              //             context,
              //             MaterialPageRoute(builder: (context) =>
              //              MyHomePage()
              //             ));
              Navigator.of(context).pushNamedAndRemoveUntil("signIn", (route) => false);


            }

          },
          child: Container(
              margin: EdgeInsets.only(top: 100.h,left: 25.w,right: 25.w),
              height: 50.h,
              width: 340.w,
              decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(14),
                  color:  AppColors.primaryElement,
                  boxShadow: [
                    BoxShadow(
                        color: Colors.grey.withOpacity(.9),
                        offset:Offset (0,5),
                        blurRadius: 2,
                        spreadRadius: 1
                    )
                  ]
              ),
              child:Center(
                child: Text(buttonName,style: TextStyle(
                    color: Colors.white,fontWeight: FontWeight.normal,
                    fontSize: 16.h
                ),),
              )
          ),
        )
      ],
    );
  }
}
